import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminPanel = ({ profile, onUpdate }) => {
  const [formData, setFormData] = useState({
    name: "",
    title: "",
    description: "",
    about_me: "",
    email: "",
    phone: "",
    location: "",
    social_links: {
      linkedin: "",
      github: "",
      twitter: "",
      instagram: "",
      facebook: "",
      website: ""
    },
    skills: [],
    experience: [],
    projects: []
  });

  const [newSkill, setNewSkill] = useState({ name: "", level: 50, category: "technical" });
  const [newExperience, setNewExperience] = useState({
    company: "",
    position: "",
    start_date: "",
    end_date: "",
    description: "",
    technologies: []
  });
  const [newProject, setNewProject] = useState({
    title: "",
    description: "",
    technologies: [],
    github_url: "",
    live_url: ""
  });

  const [activeTab, setActiveTab] = useState("basic");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Helper function to check if a value is a default placeholder
  const isDefaultValue = (value, defaultText) => {
    return value === defaultText || value === `${defaultText}`;
  };

  useEffect(() => {
    if (profile) {
      setFormData({
        name: isDefaultValue(profile.name, "Ð’Ð°ÑˆÐµ Ð¸Ð¼Ñ") ? "" : profile.name || "",
        title: isDefaultValue(profile.title, "Ð’Ð°ÑˆÐ° Ð¿Ñ€Ð¾Ñ„ÐµÑÑÐ¸Ñ") ? "" : profile.title || "",
        description: isDefaultValue(profile.description, "ÐšÑ€Ð°Ñ‚ÐºÐ¾Ðµ Ð¾Ð¿Ð¸ÑÐ°Ð½Ð¸Ðµ Ð¾ ÑÐµÐ±Ðµ") ? "" : profile.description || "",
        about_me: isDefaultValue(profile.about_me, "Ð Ð°ÑÑÐºÐ°Ð¶Ð¸Ñ‚Ðµ Ð¾ ÑÐµÐ±Ðµ Ð¿Ð¾Ð´Ñ€Ð¾Ð±Ð½ÐµÐµ...") ? "" : profile.about_me || "",
        email: isDefaultValue(profile.email, "your@email.com") ? "" : profile.email || "",
        phone: isDefaultValue(profile.phone, "+7 (999) 123-45-67") ? "" : profile.phone || "",
        location: isDefaultValue(profile.location, "Ð“Ð¾Ñ€Ð¾Ð´, Ð¡Ñ‚Ñ€Ð°Ð½Ð°") ? "" : profile.location || "",
        social_links: {
          linkedin: profile.social_links?.linkedin || "",
          github: profile.social_links?.github || "",
          twitter: profile.social_links?.twitter || "",
          instagram: profile.social_links?.instagram || "",
          facebook: profile.social_links?.facebook || "",
          website: profile.social_links?.website || ""
        },
        skills: profile.skills || [],
        experience: profile.experience || [],
        projects: profile.projects || []
      });
    }
  }, [profile]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("social_")) {
      const socialKey = name.replace("social_", "");
      setFormData(prev => ({
        ...prev,
        social_links: {
          ...prev.social_links,
          [socialKey]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      await axios.put(`${API}/profile`, formData);
      setMessage("ÐŸÑ€Ð¾Ñ„Ð¸Ð»ÑŒ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½!");
      onUpdate();
    } catch (error) {
      setMessage("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ð¸ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»Ñ");
      console.error("Error updating profile:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = async (e, imageType) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);
    formData.append("image_type", imageType);

    try {
      const response = await axios.post(`${API}/profile/upload-image`, formData, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      });
      
      setMessage(`${imageType === 'avatar' ? 'ÐÐ²Ð°Ñ‚Ð°Ñ€' : 'Ð¤Ð¾Ð½Ð¾Ð²Ð¾Ðµ Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ'} ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ð¾!`);
      onUpdate();
    } catch (error) {
      setMessage("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ");
      console.error("Error uploading image:", error);
    }
  };

  const addSkill = () => {
    if (newSkill.name.trim()) {
      setFormData(prev => ({
        ...prev,
        skills: [...prev.skills, { ...newSkill, id: Date.now().toString() }]
      }));
      setNewSkill({ name: "", level: 50, category: "technical" });
    }
  };

  const removeSkill = (index) => {
    setFormData(prev => ({
      ...prev,
      skills: prev.skills.filter((_, i) => i !== index)
    }));
  };

  const addExperience = () => {
    if (newExperience.company.trim() && newExperience.position.trim()) {
      setFormData(prev => ({
        ...prev,
        experience: [...prev.experience, { ...newExperience, id: Date.now().toString() }]
      }));
      setNewExperience({
        company: "",
        position: "",
        start_date: "",
        end_date: "",
        description: "",
        technologies: []
      });
    }
  };

  const removeExperience = (index) => {
    setFormData(prev => ({
      ...prev,
      experience: prev.experience.filter((_, i) => i !== index)
    }));
  };

  const addProject = () => {
    if (newProject.title.trim() && newProject.description.trim()) {
      setFormData(prev => ({
        ...prev,
        projects: [...prev.projects, { ...newProject, id: Date.now().toString() }]
      }));
      setNewProject({
        title: "",
        description: "",
        technologies: [],
        github_url: "",
        live_url: ""
      });
    }
  };

  const removeProject = (index) => {
    setFormData(prev => ({
      ...prev,
      projects: prev.projects.filter((_, i) => i !== index)
    }));
  };

  const handleTechnologiesChange = (value, type, setterFunction) => {
    const technologies = value.split(",").map(tech => tech.trim()).filter(tech => tech);
    setterFunction(prev => ({ ...prev, technologies }));
  };

  const tabs = [
    { id: "basic", label: "ÐžÑÐ½Ð¾Ð²Ð½Ð¾Ðµ", icon: "ðŸ‘¤" },
    { id: "images", label: "Ð˜Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ", icon: "ðŸ–¼ï¸" },
    { id: "social", label: "Ð¡Ð¾Ñ†Ð¸Ð°Ð»ÑŒÐ½Ñ‹Ðµ ÑÐµÑ‚Ð¸", icon: "ðŸ”—" },
    { id: "skills", label: "ÐÐ°Ð²Ñ‹ÐºÐ¸", icon: "ðŸ’ª" },
    { id: "experience", label: "ÐžÐ¿Ñ‹Ñ‚", icon: "ðŸ’¼" },
    { id: "projects", label: "ÐŸÑ€Ð¾ÐµÐºÑ‚Ñ‹", icon: "ðŸš€" }
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">ÐŸÐ°Ð½ÐµÐ»ÑŒ Ð°Ð´Ð¼Ð¸Ð½Ð¸ÑÑ‚Ñ€Ð°Ñ‚Ð¾Ñ€Ð°</h1>
          <Link
            to="/"
            className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-md text-sm font-medium transition-colors"
          >
            Ð’ÐµÑ€Ð½ÑƒÑ‚ÑŒÑÑ Ð½Ð° ÑÐ°Ð¹Ñ‚
          </Link>
        </div>

        {message && (
          <div className={`mb-6 p-4 rounded-md ${
            message.includes("ÑƒÑÐ¿ÐµÑˆÐ½Ð¾") ? "bg-green-600" : "bg-red-600"
          }`}>
            {message}
          </div>
        )}

        <div className="bg-gray-800 rounded-lg shadow-xl">
          {/* Tab Navigation */}
          <div className="border-b border-gray-700">
            <nav className="flex space-x-8 px-6" aria-label="Tabs">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === tab.id
                      ? "border-blue-500 text-blue-400"
                      : "border-transparent text-gray-300 hover:text-gray-200 hover:border-gray-300"
                  }`}
                >
                  <span className="mr-2">{tab.icon}</span>
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>

          <form onSubmit={handleSubmit} className="p-6">
            {/* Basic Information Tab */}
            {activeTab === "basic" && (
              <div className="space-y-6">
                <h2 className="text-xl font-semibold mb-4">ÐžÑÐ½Ð¾Ð²Ð½Ð°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ</h2>
                
                <div className="grid md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Ð˜Ð¼Ñ
                    </label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Ð’Ð°ÑˆÐµ Ð¸Ð¼Ñ"
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      ÐŸÑ€Ð¾Ñ„ÐµÑÑÐ¸Ñ/Ð”Ð¾Ð»Ð¶Ð½Ð¾ÑÑ‚ÑŒ
                    </label>
                    <input
                      type="text"
                      name="title"
                      value={formData.title}
                      onChange={handleInputChange}
                      className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Ð’Ð°ÑˆÐ° Ð¿Ñ€Ð¾Ñ„ÐµÑÑÐ¸Ñ"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    ÐšÑ€Ð°Ñ‚ÐºÐ¾Ðµ Ð¾Ð¿Ð¸ÑÐ°Ð½Ð¸Ðµ
                  </label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    rows={3}
                    className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="ÐšÑ€Ð°Ñ‚ÐºÐ¾Ðµ Ð¾Ð¿Ð¸ÑÐ°Ð½Ð¸Ðµ Ð¾ ÑÐµÐ±Ðµ"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    ÐŸÐ¾Ð´Ñ€Ð¾Ð±Ð½Ð¾ Ð¾Ð±Ð¾ Ð¼Ð½Ðµ
                  </label>
                  <textarea
                    name="about_me"
                    value={formData.about_me}
                    onChange={handleInputChange}
                    rows={5}
                    className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Ð Ð°ÑÑÐºÐ°Ð¶Ð¸Ñ‚Ðµ Ð¾ ÑÐµÐ±Ðµ Ð¿Ð¾Ð´Ñ€Ð¾Ð±Ð½ÐµÐµ..."
                  />
                </div>

                <div className="grid md:grid-cols-3 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Email
                    </label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder="your@email.com"
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Ð¢ÐµÐ»ÐµÑ„Ð¾Ð½
                    </label>
                    <input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
